function loadHTML(myDivId) {
   (document.getElementById(myDivId).innerHTML = htmlText); 
}

var injectDiv = "embed-whatsapp-chat",
    injectDivElement,
    htmlText ='';
    root=':root { --chat-primary-color: {{$button_color}}; --chat-header-color: {{ $header_color }}; --chat-wrapper-bg: linear-gradient(135deg, #667eea 0%, #764ba2 100%); --chat-bubble-bg: #ffffff; --chat-text-color: #374151; --chat-text-light: #6b7280; --chat-border-radius: 20px; --chat-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25); --chat-shadow-light: 0 10px 15px -3px rgba(0, 0, 0, 0.1); }'
    rules='.mobidonia_whatsapp_popup { font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif; position: relative; z-index: 999999; } #whatsAppChatClick { display: none; } #whatsAppChatOpener { position: fixed; right: 24px; bottom: 24px; width: 60px; height: 60px; background: var(--chat-primary-color); border-radius: 50%; cursor: pointer; display: flex; align-items: center; justify-content: center; box-shadow: var(--chat-shadow); transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); z-index: 1000; border: none; opacity: 0; transform: scale(0.8); } #whatsAppChatOpener:hover { transform: scale(1.1); box-shadow: 0 32px 64px -12px rgba(0, 0, 0, 0.35); } #whatsAppChatOpener:active { transform: scale(0.95); } .whatsAppChatBadge { position: absolute; top: -2px; right: -2px; width: 20px; height: 20px; background: #ef4444; border: 3px solid white; border-radius: 50%; animation: pulse 2s infinite; } @keyframes pulse { 0%, 100% { opacity: 1; } 50% { opacity: 0.5; } } #whatsAppChatOpener i { position: absolute; transition: all 0.3s ease; } #whatsAppChatOpener i img { width: 28px; height: 28px; filter: brightness(0) invert(1); } #whatsAppChatOpener i.mob_whats_opened { opacity: 0; transform: rotate(45deg) scale(0.8); } #whatsAppChatClick:checked ~ #whatsAppChatOpener i.mob_whats_opened { opacity: 1; transform: rotate(0deg) scale(1); } #whatsAppChatClick:checked ~ #whatsAppChatOpener i.mob_whats_closed { opacity: 0; transform: rotate(-45deg) scale(0.8); } .whatsAppChatWrapper { position: fixed; right: 24px; bottom: 24px; width: 380px; max-height: 600px; background: white; border-radius: var(--chat-border-radius); box-shadow: var(--chat-shadow); opacity: 0; pointer-events: none; transform: translateY(20px) scale(0.95); transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1); overflow: hidden; backdrop-filter: blur(10px); } #whatsAppChatClick:checked ~ .whatsAppChatWrapper { opacity: 1; pointer-events: auto; transform: translateY(-80px) scale(1); } .whatsAppChatHeader { background: var(--chat-header-color); padding: 20px; display: flex; align-items: center; gap: 12px; position: relative; } .whatsAppChatHeader::before { content: ""; position: absolute; inset: 0; background: linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%); } .whatsAppChatVendor { position: relative; z-index: 1; } .whatsAppChatVendorLogo { width: 48px; height: 48px; border-radius: 50%; overflow: hidden; background: rgba(255,255,255,0.1); backdrop-filter: blur(10px); border: 2px solid rgba(255,255,255,0.2); position: relative; } .whatsAppChatVendorLogo img { width: 100%; height: 100%; object-fit: cover; } .whatsAppChatStatus { position: absolute; bottom: -2px; right: -2px; width: 16px; height: 16px; background: #10b981; border: 3px solid white; border-radius: 50%; box-shadow: 0 0 0 2px rgba(16, 185, 129, 0.3); } .whatsAppChatTitle { flex: 1; position: relative; z-index: 1; } .whatsAppChatName { color: white; font-size: 18px; font-weight: 600; margin: 0; line-height: 1.2; } .whatsAppChatStatusText { color: rgba(255,255,255,0.9); font-size: 14px; font-weight: 400; margin: 2px 0 0 0; } .whatsAppChatBox { padding: 24px; background: #f8fafc; position: relative; min-height: 120px; display: flex; flex-direction: column; justify-content: flex-end; } .whatsAppChatBubble { background: var(--chat-bubble-bg); padding: 12px 16px; border-radius: 18px 18px 18px 4px; position: relative; box-shadow: var(--chat-shadow-light); margin-bottom: 8px; max-width: 85%; align-self: flex-start; } .whatsAppChatBubble::before { content: ""; position: absolute; bottom: 0; left: -8px; width: 0; height: 0; border: 8px solid transparent; border-right-color: var(--chat-bubble-bg); border-bottom-color: var(--chat-bubble-bg); border-bottom-right-radius: 4px; } .whatsAppChatMessage { color: var(--chat-text-color); font-size: 15px; line-height: 1.4; margin: 0; } .whatsAppChatMessageTime { color: var(--chat-text-light); font-size: 12px; margin-top: 4px; text-align: left; } .whatsAppChatFooter { padding: 20px; background: white; border-top: 1px solid #e5e7eb; } .whatsAppChatButton { width: 100%; background: var(--chat-primary-color); color: white; border: none; border-radius: 12px; padding: 14px 20px; font-size: 16px; font-weight: 600; display: flex; align-items: center; justify-content: center; gap: 8px; cursor: pointer; transition: all 0.2s ease; position: relative; overflow: hidden; } .whatsAppChatButton::before { content: ""; position: absolute; inset: 0; background: linear-gradient(135deg, rgba(255,255,255,0.2) 0%, rgba(255,255,255,0.1) 100%); opacity: 0; transition: opacity 0.2s ease; } .whatsAppChatButton:hover::before { opacity: 1; } .whatsAppChatButton:hover { transform: translateY(-1px); box-shadow: 0 10px 20px rgba(0,0,0,0.15); } .whatsAppChatButton:active { transform: translateY(0); } .whatsAppChatButtonIcon img { width: 20px; height: 20px; filter: brightness(0) invert(1); } @media (max-width: 480px) { .whatsAppChatWrapper { width: calc(100vw - 32px); right: 16px; } #whatsAppChatOpener { right: 16px; bottom: 16px; } }';
    style='<style>'+root+rules+'</style>';
    body='<div class="mobidonia_whatsapp_popup"> <input type="checkbox" id="whatsAppChatClick"> <label for="whatsAppChatClick" id="whatsAppChatOpener"> <div class="whatsAppChatBadge"></div> <i class="mob_whats_closed"> <img src="{{ $url}}mob_whats_closed.svg" alt="Open Chat"> </i> <i class="mob_whats_opened"> <img src="{{ $url }}mob_whats_open.svg" alt="Close Chat"> </i> </label> <div class="whatsAppChatWrapper"> <div class="whatsAppChatHeader"> <div class="whatsAppChatVendor"> <div class="whatsAppChatVendorLogo"> <img src="{{$logo}}" alt="{{ $header_text }}"> </div> <div class="whatsAppChatStatus"></div> </div> <div class="whatsAppChatTitle"> <div class="whatsAppChatName">{{ $header_text }}</div> <div class="whatsAppChatStatusText">{{ $header_subtext }}</div> </div> </div> <div class="whatsAppChatBox"> <div class="whatsAppChatBubble"> <div class="whatsAppChatMessage"> <div id="theMessage"></div> <div class="whatsAppChatMessageTime" id="currentTime"></div> </div> </div> </div> <div class="whatsAppChatFooter"> <button onclick="window.open(\'{{$chatlink}}\')" class="whatsAppChatButton"> <div class="whatsAppChatButtonIcon"> <img src="{{ $url }}whatsapp.svg" alt="WhatsApp"> </div> <span>{{ $button_text }}</span> </button> </div> </div> </div>';
    htmlText += style+body;
   

    document.addEventListener("DOMContentLoaded", function (t) {
        (injectDivElement = document.getElementById(injectDiv)),
        loadHTML(injectDiv);
        
        // Set the content of theMessage to be message
        var message= `{{ json_encode($message) }}`;
        message = message.replace(/\n/g, "<br />");
        message = message.replace(/&quot;/g, "");
        document.getElementById('theMessage').innerHTML = message;
        
        // Set current time
        var now = new Date();
        var timeString = now.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
        document.getElementById('currentTime').innerHTML = timeString;
        
        // Add smooth entrance animation
        setTimeout(function() {
            var opener = document.getElementById('whatsAppChatOpener');
            if (opener) {
                opener.style.opacity = '1';
                opener.style.transform = 'scale(1)';
            }
        }, 500);
    });